import React, { useState, Fragment } from "react";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { makeStyles } from '@material-ui/core/styles';

//테이블 작업용 import



function createData(years, principal, interest, result) {
  return {years, principal, interest, result}
}

const CompoundInterest = () => {
    const [principal, setPrincipal] = useState();
    const [years, setYears] = useState();
    const [interest, setInterest] = useState();
    const [result, setResult] = useState();

    const calculate = () => {
      // A=p(1+(r/n))^(nt)
      const result = principal * Math.pow(1 + interest, years);
      setResult(result.toFixed(2));  
    };

    function resultTable() {
      const principal = document.getElementById('principal').value;
      const interest = document.getElementById('interest').value;
      const years = document.getElementById('years').value;
      interest = 1+(interest/100);//convert to Dec.
      const tableTag='<table>';
      alert(interest);
      const result = principal;
      for (i=1; i<years; i++) {
        const res = principal * (Math.pow(interest, i));
        res = res.toFixed(2);
        interestGained = +res - +principal;
        interestGained = interestGained.toFixed(2)
        tableTag = tableTag + "<tr>Year " + 1 + "| Principal: " + principal + "USD | Total: " + res + "USD | Interest: " + interestGained + "USD</tr><br />";
      }

      tableTag = tableTag + "</table>";
      alert(tableTag);
      document.getElementById("someDIV").innerHTML = tableTag;
      }

//Dynamic inputting table row
//연도 시작값 =0, Break는 설정한 연도    

return (
      <>
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            height: "40vh",
          }}
        >
          <form>
            <TextField
              label="설정금액(투자원금)"
              variant="outlined"
              type="number"
              onChange={(e) => setPrincipal(e.target.value)}
            />
            <TextField
              label="투자기간(연)"
              variant="outlined"
              type="number"
              onChange={(e) => setYears(e.target.value)}
            />
            <TextField
              label="연평균 목표수익률(%)"
              variant="outlined"
              type="number"
              onChange={(e) => setInterest(e.target.value / 100)}
            />
            <br />
            <br />
            <Button
              variant="contained"
              color="primary"
              onClick={() => {
                calculate();
              }}
            >
              계산하기!
            </Button>
            <br />
            <div style={{ fontSize: "30px" }}>금액 : {result}</div>
            {/* 일단 요기에다가 테이블작업 시작*/}
            <br />

          </form>
        </div>  
      </>
    );
  };


export default CompoundInterest
