import React, { useState, Fragment } from "react";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { makeStyles } from '@material-ui/core/styles';

//테이블 작업용 import
import Header from './Header';
import Table from './Table';
import PropTypes from 'prop-types';


function createData(years, principal, interest, result) {
  return {years, principal, interest, result}
}

const CompoundInterest = () => {
    const [principal, setPrincipal] = useState();
    const [years, setYears] = useState();
    const [interest, setInterest] = useState();
    const [result, setResult] = useState();

    const investmentData = [
      {
      years: setYears,
      principal: setPrincipal,
      interest: setInterest,
      result: setResult
      }
    ]
    //테이블 생성 반복문

    const data = printTable.map = ((row, index) => {
      let rowData = [];

      for(let i=0; i===years; i++) {
        if(i===0){
          rowData.push({
            years: [i],
            principal: '-',
            interest: '-',
            result: [setPrincipal+(setPrincipal*setInterest)]
          });
        }
        else {
          rowData.push({
            years: [i],
            principal: [result],
            interest: [setInterest],
            result: [setPrincipal+(setPrincipal*setInterest)]
          });
        }
      return <tr key={index}>
        {rowData.map((data, index) => <td key={index} data-heading={data.key}>{data.val}</td>)}
      </tr>
      }
    })


    const calculate = () => {
      // A=p(1+(r/n))^(nt)
      const result = principal * Math.pow(1 + interest, years);
      setResult(result.toFixed(2));  
    };

//Dynamic inputting table row
//연도 시작값 =0, Break는 설정한 연도    

return (
      <>
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            height: "40vh",
          }}
        >
          <form>
            <TextField
              label="설정금액(투자원금)"
              variant="outlined"
              type="number"
              onChange={(e) => setPrincipal(e.target.value)}
            />
            <TextField
              label="투자기간(연)"
              variant="outlined"
              type="number"
              onChange={(e) => setYears(e.target.value)}
            />
            <TextField
              label="연평균 목표수익률(%)"
              variant="outlined"
              type="number"
              onChange={(e) => setInterest(e.target.value / 100)}
            />
            <br />
            <br />
            <Button
              variant="contained"
              color="primary"
              onClick={() => {
                calculate();
              }}
            >
              계산하기!
            </Button>
            <br />
            <div style={{ fontSize: "30px" }}>금액 : {result}</div>
            {/* 일단 요기에다가 테이블작업 시작*/}
            <br />
            <div>
                <Fragment>
                  <Header title="연간 복리 성장표" />
                  <Table 
                    tableData={printTable}
                    headingColumns={['연차', '원금', '수익률', '평가금액']}
                  />
                </Fragment>
            </div>
          </form>
        </div>  
      </>
    );
  };


export default CompoundInterest
